SET NOCOUNT ON;
INSERT INTO [AppUserRoles] ([ApplicationUserId], [Created], [CreatedBy], [LastModified], [LastModifiedBy], [canAdd], [canDelete], [canUpdate], [canView], [canViewDetail], [page], [title])
VALUES (@p0, @p1, @p2, @p3, @p4, @p5, @p6, @p7, @p8, @p9, @p10, @p11);
SELECT [Id]
FROM [AppUserRoles]
WHERE @@ROWCOUNT = 1 AND [Id] = scope_identity();
